import { useRef, useState, useEffect} from 'react';
import { StyleSheet, Text, View, SafeAreaView, Button, Image } from 'react-native';
import { Camera } from 'expo-camera';
import { shareAsync } from 'expo-sharing';
import * as MediaLibrary from 'expo-media-library';
import * as ImagePicker from 'expo-image-picker';
import { StatusBar } from 'expo-status-bar';

export default function App() {  
  let cameraRef = useRef();
  const [hasCameraPermission, setHasCameraPermission] = useState();
  const [hasMediaLibraryPermission, setHasMediaLibraryPermission] = useState();
  const [photo, setPhoto] = useState();
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    (async () => {
      const cameraPermission = await Camera.requestCameraPermissionsAsync();
      const mediaLibraryPermission = await MediaLibrary.requestPermissionsAsync();
      const imagePickerPermission = await ImagePicker.requestMediaLibraryPermissionsAsync();
      setHasCameraPermission(cameraPermission.status === "granted");
      setHasMediaLibraryPermission(mediaLibraryPermission.status === "granted");
      setHasImagePickerPermission(imagePickerPermission.status === "granted");
    })();
  }, []);

  if (hasCameraPermission === undefined || hasMediaLibraryPermission === undefined || hasImagePickerPermission === undefined) {
    return <Text>Requesting Permissions...</Text>
  } else if (!hasCameraPermission || !hasMediaLibraryPermission || !hasImagePickerPermission) {
    return <Text>Permission for camera or media library not granted. Please change this in settings</Text>
  }

  let takePic = async() => {
    let options = {
      quality: 1,
      base64: true,
      exif: false
    };

    let newPhoto = await cameraRef.current.takePictureAsync(options);
    setPhoto(newPhoto);
  };

  let pickImage = async() => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      base64: true,
    });

    if (!result.cancelled) {
      setSelectedImage(result.uri);
    }
  };

  if (photo || selectedImage) {
    let sharePic = () => {
      let uri = photo ? photo.uri : selectedImage;
      shareAsync(uri).then(() => {
        setPhoto(undefined);
        setSelectedImage(null);
      })
    };
    let savePhoto = () => {
      let uri = photo ? photo.uri : selectedImage;
      MediaLibrary.saveToLibraryAsync(uri).then(() => {
        setPhoto(undefined);
        setSelectedImage(null);
      })
    };
    let sendPhoto = () => {
      let uri = photo ? photo.uri : selectedImage;
      if (uri) {
        let formData = new FormData();
        formData.append('photo', uri);

        fetch('http://localhost:3000/photo', {
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'multipart/form-data'
          },
          body: formData
        })
        .then(response => response.json())
        .then(data => {
          console.log(data.message);
        })
        .catch(error => {
          console.error(error);
        });
        setPhoto(undefined);
        setSelectedImage(null);
      }
    };

    eturn (
      <>
      <View style={styles.preview}>
      <Image source={{uri: photo.uri}} style={styles.previewImage}/>
      </View>
      <View style={styles.buttonContainer}>
      <Button title="Share" onPress={sharePic} />
      <Button title="Save" onPress={savePhoto} />
      <Button title="Send" onPress={sendPhoto} />
      </View>
      </>
      );
      } else if (selectedImage) {
      return (
      <SafeAreaView style={styles.container}>
      <Image source={{uri: selectedImage}} style={styles.selectedImage} />
      <View style={styles.buttonContainer}>
      <Button title="Share" onPress={sharePic} />
      <Button title="Save" onPress={savePhoto} />
      <Button title="Send" onPress={sendPhoto} />
      </View>
      </SafeAreaView>
      )
      } else {
      return (
      <SafeAreaView style={styles.container}>
      <StatusBar style="auto" />
      <View style={styles.cameraContainer}>
      <Camera style={styles.camera} type={Camera.Constants.Type.back} ref={cameraRef}>
      <View style={styles.buttonContainer}>
      <Button title="Take Picture" onPress={takePic}/>
      </View>
      </Camera>
      </View>
      <View style={styles.buttonContainer}>
      <Button title="Pick Image" onPress={pickImage}/>
      </View>
      </SafeAreaView>
      );
      }
      }

      const styles = StyleSheet.create({
      container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      },
      cameraContainer: {
      flex: 1,
      width: '100%',
      },
      camera: {
      flex: 1,
      },
      buttonContainer: {
      flexDirection: 'row',
      margin: 20,
      },
      preview: {
      flex: 1,
      justifyContent: 'flex-end',
      alignItems: 'center',
      },
      previewImage: {
      height: '80%',
      width: '100%',
      resizeMode: 'contain',
      },
      selectedImage: {
      height: '80%',
      width: '100%',
      resizeMode: 'contain',
      }
      });










